package com.clt.xml;import org.xml.sax.Attributes;import org.xml.sax.SAXException;public interface XMLHandler {  public String getElementName();  public void startElement(String namespaceURI, String localName, String qName, Attributes atts)        throws SAXException;  public void endElement(String namespaceURI, String localName, String qName)      throws SAXException;  public void characters(char[] ch, int start, int length)      throws SAXException;}